import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function AddExperiences() {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [duration, setDuration] = useState('');
  const [price, setPrice] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // placeholder: in future send to API
    alert(`Created experience: ${title}`);
    // reset
    setTitle(''); setDesc(''); setDuration(''); setPrice('');
  };

  return (
    <div className="container admin-container" style={{ background: 'black', color: 'white', minHeight: '60vh', padding: 20 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div className="brand"><h2 className="brand-heading">Add Experiences</h2></div>
        <div>
          <button className="back-btn" onClick={() => navigate('/admin')}>Back</button>
        </div>
      </div>

      <form onSubmit={handleSubmit} style={{ marginTop: 12 }}>
        <div style={{ marginBottom: 8 }}>
          <label style={{ display: 'block', marginBottom: 6 }}>Title</label>
          <input value={title} onChange={e => setTitle(e.target.value)} style={{ width: '100%', padding: 8, borderRadius: 8 }} />
        </div>
        <div style={{ marginBottom: 8 }}>
          <label style={{ display: 'block', marginBottom: 6 }}>Description</label>
          <textarea value={desc} onChange={e => setDesc(e.target.value)} style={{ width: '100%', padding: 8, borderRadius: 8 }} />
        </div>
        <div style={{ display: 'flex', gap: 8, marginBottom: 8 }}>
          <input placeholder="Duration" value={duration} onChange={e => setDuration(e.target.value)} style={{ flex: 1, padding: 8, borderRadius: 8 }} />
          <input placeholder="Price" value={price} onChange={e => setPrice(e.target.value)} style={{ flex: 1, padding: 8, borderRadius: 8 }} />
        </div>
        <div style={{ display: 'flex', gap: 8 }}>
          <button type="submit" style={{ background: 'var(--brand-green)', color: 'white', padding: '8px 12px', borderRadius: 8 }}>Create</button>
          <button type="button" onClick={() => navigate('/admin')} style={{ padding: '8px 12px', borderRadius: 8 }}>Cancel</button>
        </div>
      </form>
    </div>
  );
}
