import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllUsers, deleteUser } from '../services/api';

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [searchBy, setSearchBy] = useState('name');
  const [deletingId, setDeletingId] = useState(null);

  useEffect(() => {
    let mounted = true;
    async function load() {
      setLoading(true);
      setError(null);
      try {
        const res = await getAllUsers();
        if (!mounted) return;
        setUsers(res.users || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    load();
    return () => { mounted = false; };
  }, []);

  return (
    <div className="container" style={{ background: 'black', color: 'white', minHeight: '60vh', padding: 20 }}>
      <div className="brand">
        <h2 className="brand-heading">Near-me Adventures</h2>
      </div>
      <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8, flexDirection: 'column' }}>
        <button onClick={() => navigate('/analysis')} style={{ padding: '6px 10px' }}>Analytics</button>
        <button onClick={() => navigate('/add-experiences')} style={{ padding: '6px 10px', marginTop: 8 }}>Experiences Manage (add/view/filter)</button>
      </div>
      <h1 style={{ color: 'red' }}>Admin Dashboard</h1>
      <p>Summary for administrators.</p>

      {loading && <p>Loading users...</p>}
      {error && <p style={{ color: 'crimson' }}>{error}</p>}

      {!loading && !error && (
        <div>
          <p><strong>Total users:</strong> {users.length}</p>

          <div style={{ marginTop: 12, display: 'flex', gap: 8, alignItems: 'center' }}>
            <select value={searchBy} onChange={e => setSearchBy(e.target.value)} style={{ padding: 8, borderRadius: 8 }}>
              <option value="name">Name</option>
              <option value="email">Email</option>
            </select>
            <input
              placeholder={searchBy === 'email' ? 'Search by email' : 'Search by name'}
              value={search}
              onChange={e => setSearch(e.target.value)}
              style={{ padding: 8, width: '100%', maxWidth: 400, borderRadius: 8 }}
            />
            <button onClick={() => setSearch('')} style={{ padding: 8, borderRadius: 8 }}>Clear</button>
          </div>

          <div style={{ overflowX: 'auto', marginTop: 12 }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', color: 'white' }}>
              <thead>
                <tr>
                  <th style={{ textAlign: 'left', borderBottom: '1px solid #444', padding: 8 }}>Name</th>
                  <th style={{ textAlign: 'left', borderBottom: '1px solid #444', padding: 8 }}>Email</th>
                  
                  <th style={{ textAlign: 'left', borderBottom: '1px solid #444', padding: 8 }}>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users
                  .filter(u => {
                    if (!search) return true;
                    const q = search.toLowerCase();
                    if (searchBy === 'email') return (u.email || '').toLowerCase().includes(q);
                    return (u.name || '').toLowerCase().includes(q);
                  })
                  .map(u => (
                  <tr key={u._id}>
                    <td style={{ padding: 8, borderBottom: '1px solid #222' }}>{u.name}</td>
                    <td style={{ padding: 8, borderBottom: '1px solid #222' }}>{u.email}</td>
                    
                    <td style={{ padding: 8, borderBottom: '1px solid #222' }}>
                      <button
                        onClick={async () => {
                          if (!window.confirm(`Delete user ${u.name} (${u.email})?`)) return;
                          try {
                            setDeletingId(u._id);
                            await deleteUser(u._id);
                            setUsers(prev => prev.filter(x => x._id !== u._id));
                          } catch (err) {
                            alert(err.message || 'Delete failed');
                          } finally {
                            setDeletingId(null);
                          }
                        }}
                        disabled={deletingId === u._id}
                        style={{ background: '#c0392b', color: 'white', border: 'none', padding: '6px 10px', cursor: 'pointer' }}
                      >{deletingId === u._id ? 'Deleting...' : 'Delete'}</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      <div style={{ marginTop: 12 }}>
        <button onClick={() => { localStorage.removeItem('token'); navigate('/login'); }}>Logout</button>
      </div>
    </div>
  );
}
