import React, { useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import Card from '../components/Card';
import sample from '../data/places';

export default function Browse() {
  const navigate = useNavigate();
  const [q, setQ] = useState('');
  const [sortAsc, setSortAsc] = useState(true);

  const parsePrice = (priceStr) => {
    if (!priceStr) return 0;
    const s = String(priceStr).trim();
    if (/free/i.test(s)) return 0;
    // remove commas and non-numeric/dot/–- characters, handle ranges by taking first number
    const cleaned = s.replace(/,/g, '').replace(/[^0-9.\-–]/g, ' ');
    const m = cleaned.match(/(\d+[\d\.]*)/);
    return m ? Number(m[0]) : 0;
  };

  const filtered = useMemo(() => {
    const term = q.trim().toLowerCase();
    if (!term) return sample;
    const list = sample.filter(s => (`${s.title} ${s.desc}`.toLowerCase().includes(term)));
    // sort by parsed price
    return list.sort((a, b) => {
      const pa = parsePrice(a.price);
      const pb = parsePrice(b.price);
      return sortAsc ? pa - pb : pb - pa;
    });
  }, [q]);

  // when no search term, also sort the full sample according to sortAsc
  const displayed = useMemo(() => {
    const list = filtered.slice();
    return list.sort((a, b) => {
      const pa = parsePrice(a.price);
      const pb = parsePrice(b.price);
      return sortAsc ? pa - pb : pb - pa;
    });
  }, [filtered, sortAsc]);

  // suggestions: only match titles that start with the query (prefix matching)
  const suggestions = useMemo(() => {
    const term = q.trim().toLowerCase();
    if (!term) return [];
    return sample
      .map(s => s.title)
      .filter(title => title.toLowerCase().startsWith(term));
  }, [q]);

  return (
    <div className="page">
      <div className="page-header">
        <div className="brand">
          <h2 className="brand-heading">Browse Experiences</h2>
          <h4 className="brand-heading">Discover unique activities around you (Premiums and best Ones)</h4>
        </div>
        <div style={{ textAlign: 'right' }}>
          <button onClick={() => navigate('/')}>Back</button>
          <button style={{ marginLeft: 8 }} onClick={() => navigate('/all-places')}>All Places</button>
        </div>
      </div>

      <div className="search-row">
        <input
          className="search-input"
          value={q}
          onChange={e => setQ(e.target.value)}
          placeholder="Search experiences"
          aria-label="Search experiences"
        />
        <button onClick={() => setSortAsc(s => !s)} style={{ marginLeft: 8, padding: '8px 10px', borderRadius: 8 }}>
          {sortAsc ? 'Price ↑' : 'Price ↓'}
        </button>
        {suggestions.length > 0 && (
          <ul className="suggestions" role="listbox">
            {suggestions.map((s, idx) => (
              <li key={idx} className="suggestion-item" role="option">
                <button type="button" onClick={() => setQ(s)}>{s}</button>
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="grid">
        {displayed.length > 0 ? (
          displayed.map(item => (
            <Card key={item.id} {...item} />
          ))
        ) : (
          <div className="container">
            <p>No experiences found.</p>
          </div>
        )}
      </div>
    </div>
  );
}
