import React, { useState } from 'react';

export default function Card({ img, title, desc, duration, price, rating }) {
  const svg = `<svg xmlns="http://www.w3.org/2000/svg" width="800" height="600"><rect width="100%" height="100%" fill="#f8fafc"/><text x="50%" y="50%" dominant-baseline="middle" text-anchor="middle" fill="#9ca3af" font-family="Arial, Helvetica, sans-serif" font-size="20">No image</text></svg>`;
  const fallback = `data:image/svg+xml;utf8,${encodeURIComponent(svg)}`;
  const [src, setSrc] = useState(img || fallback);

  return (
    <div className="card-item">
      <img
        className="card-media"
        src={src}
        alt={title}
        onError={(e) => {
          if (e?.target?.src !== fallback) setSrc(fallback);
        }}
      />
      <div className="card-body">
        <div className="card-title">{title}</div>
        <div className="desc muted">{desc}</div>
        <div style={{ marginTop: 'auto' }}>
          <div className="card-row">
            <div className="muted">{duration}</div>
            <div className="muted">{price}</div>
          </div>
          <div className="card-row" style={{ marginTop: 8 }}>
            <div className="muted">‚≠ê {rating}</div>
            <button style={{ width: 120 }}>Book Now</button>
          </div>
        </div>
      </div>
    </div>
  );
}
