import React, { useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import Card from '../components/Card';

const sample = [
  {
    id: 1,
    title: 'Ahsan Manzil (Pink Palace)',
    desc: 'A beautifully restored Mughal-style palace museum by the Buriganga River.',
    duration: '1–2 hours',
    price: 'Free',
    rating: 4.6,
    img: 'https://upload.wikimedia.org/wikipedia/commons/1/16/Ahsan_Manzil-Front_View.jpg'
  },
  {
    id: 2,
    title: 'Lalbagh Fort',
    desc: 'A historic 17th-century Mughal fortress featuring gardens and gateways.',
    duration: '1–3 hours',
    price: '৳ 20',
    rating: 4.5,
    img: 'https://images.pexels.com/photos/17830385/pexels-photo-17830385.jpeg'
  },
  {
    id: 3,
    title: 'Shilpakala Academy',
    desc: 'A vibrant cultural center hosting theatre, dance and art exhibitions.',
    duration: '1–2 hours',
    price: '৳ 100 – 300',
    rating: 4.7,
    img: 'https://upload.wikimedia.org/wikipedia/commons/b/b5/Dhaka_Art_Summit_2023.jpg'
  },
  {
    id: 4,
    title: 'Prana Yoga Studio',
    desc: 'Boutique yoga studio offering Vinyasa flow classes and workshops.',
    duration: '1–1.5 hours',
    price: '৳ 600 – 1,200',
    rating: 4.7,
    img: 'https://images.pexels.com/photos/1051838/pexels-photo-1051838.jpeg'
  },
  {
    id: 5,
    title: 'Cooking Class',
    desc: 'Hands-on Bengali cooking class teaching biryani, bhorta and sweets.',
    duration: '2–3 hours',
    price: '৳ 800 – 1,200',
    rating: 4.6,
    img: 'https://images.pexels.com/photos/2284166/pexels-photo-2284166.jpeg'
  },
  {
    id: 6,
    title: 'Purbachal Cycling Trail',
    desc: 'A scenic lakeside cycling route with smooth paths and fresh air.',
    duration: '1–2 hours',
    price: 'Free',
    rating: 4.5,
    img: 'https://images.pexels.com/photos/35100311/pexels-photo-35100311.jpeg'
  }
];

export default function Browse() {
  const navigate = useNavigate();
  const [q, setQ] = useState('');
  const [sortAsc, setSortAsc] = useState(true);

  const parsePrice = (priceStr) => {
    if (!priceStr) return 0;
    const s = String(priceStr).trim();
    if (/free/i.test(s)) return 0;
    // remove commas and non-numeric/dot/–- characters, handle ranges by taking first number
    const cleaned = s.replace(/,/g, '').replace(/[^0-9.\-–]/g, ' ');
    const m = cleaned.match(/(\d+[\d\.]*)/);
    return m ? Number(m[0]) : 0;
  };

  const filtered = useMemo(() => {
    const term = q.trim().toLowerCase();
    if (!term) return sample;
    const list = sample.filter(s => (`${s.title} ${s.desc}`.toLowerCase().includes(term)));
    // sort by parsed price
    return list.sort((a, b) => {
      const pa = parsePrice(a.price);
      const pb = parsePrice(b.price);
      return sortAsc ? pa - pb : pb - pa;
    });
  }, [q]);

  // when no search term, also sort the full sample according to sortAsc
  const displayed = useMemo(() => {
    const list = filtered.slice();
    return list.sort((a, b) => {
      const pa = parsePrice(a.price);
      const pb = parsePrice(b.price);
      return sortAsc ? pa - pb : pb - pa;
    });
  }, [filtered, sortAsc]);

  // suggestions: only match titles that start with the query (prefix matching)
  const suggestions = useMemo(() => {
    const term = q.trim().toLowerCase();
    if (!term) return [];
    return sample
      .map(s => s.title)
      .filter(title => title.toLowerCase().startsWith(term));
  }, [q]);

  return (
    <div className="page">
      <div className="page-header">
        <div className="brand">
          <h2 className="brand-heading">Browse Experiences</h2>
          <h4 className="brand-heading">Discover unique activities around you (Premiums and best Ones)</h4>
        </div>
        <div style={{ textAlign: 'right' }}>
          <button onClick={() => navigate('/')}>Back</button>
          <button style={{ marginLeft: 8 }} onClick={() => navigate('/all-places')}>All Places</button>
        </div>
      </div>

      <div className="search-row">
        <input
          className="search-input"
          value={q}
          onChange={e => setQ(e.target.value)}
          placeholder="Search experiences"
          aria-label="Search experiences"
        />
        <button onClick={() => setSortAsc(s => !s)} style={{ marginLeft: 8, padding: '8px 10px', borderRadius: 8 }}>
          {sortAsc ? 'Price ↑' : 'Price ↓'}
        </button>
        {suggestions.length > 0 && (
          <ul className="suggestions" role="listbox">
            {suggestions.map((s, idx) => (
              <li key={idx} className="suggestion-item" role="option">
                <button type="button" onClick={() => setQ(s)}>{s}</button>
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="grid">
        {displayed.length > 0 ? (
          displayed.map(item => (
            <Card key={item.id} {...item} />
          ))
        ) : (
          <div className="container">
            <p>No experiences found.</p>
          </div>
        )}
      </div>
    </div>
  );
}
