import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getUserBookings, getUserWishlist } from '../services/api';

export default function UserAnalysis() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let mounted = true;
    async function load() {
      setLoading(true);
      setError(null);
      try {
        const [bRes, wRes] = await Promise.all([getUserBookings(id), getUserWishlist(id)]);
        if (!mounted) return;
        setBookings(bRes.bookings || []);
        setWishlist(wRes.wishlist || []);
      } catch (err) {
        setError(err.message || 'Failed to load user data');
      } finally {
        setLoading(false);
      }
    }
    load();
    return () => { mounted = false; };
  }, [id]);

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div className="brand"><h2 className="brand-heading">User Analytics</h2></div>
        <div>
          <button onClick={() => navigate('/analysis')}>Back</button>
        </div>
      </div>

      {loading && <p>Loading user data...</p>}
      {error && <p style={{ color: 'crimson' }}>{error}</p>}

      <h3>Bookings</h3>
      {bookings.length === 0 ? <p>No bookings</p> : (
        <div style={{ display: 'grid', gap: 8 }}>
          {bookings.map(b => (
            <div key={b.itemId || b.id} style={{ border: '1px solid #ddd', padding: 8 }}>
              <div style={{ fontWeight: 700 }}>{b.title}</div>
              <div className="muted">{b.duration}</div>
              <div className="muted">{b.priceLabel ? b.priceLabel : `৳ ${b.price}`}</div>
            </div>
          ))}
        </div>
      )}

      <h3 style={{ marginTop: 16 }}>Wishlist</h3>
      {wishlist.length === 0 ? <p>No wishlist items</p> : (
        <div style={{ display: 'grid', gap: 8 }}>
          {wishlist.map(w => (
            <div key={w.itemId} style={{ border: '1px solid #ddd', padding: 8 }}>
              <div style={{ fontWeight: 700 }}>{w.title}</div>
              <div className="muted">{w.duration}</div>
              <div className="muted">{w.price}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
