import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { getProfile } from '../services/api';

export default function Home() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    async function load() {
      try {
        const res = await getProfile();
        setUser(res.user);
      } catch (err) {
        // no profile -> go to login
        localStorage.removeItem('token');
        navigate('/login');
      } finally {
        setLoading(false);
      }
    }
    load();
  }, [navigate]);

  if (loading) return <div className="container"><p>Loading...</p></div>;

  return (
    <div className="page">
      <div className="page-header">
        <div>
          <div className="brand">
            <h2 className="brand-heading">Near-me Adventures</h2>
          </div>
          <div className="welcome">Welcome, {user?.name}!</div>
        </div>
        <div style={{ textAlign: 'right' }}>
          <div className="muted">{user?.email}</div>
        </div>
      </div>

      <div className="home-actions">
        <div className="action-card">
          <Link to="/browse"><button>Browse Experiences</button></Link>
        </div>
        <div className="action-card">
          <Link to="/trending"><button>Trending Experiences</button></Link>
        </div>
        <div className="action-card">
          <Link to="/dashboard"><button>User's Dashboard</button></Link>
        </div>
        <div className="action-card">
          <button style={{ marginRight: 8 }} onClick={() => navigate('/chat')}>Chat Box</button>
        </div>
        <div className="action-card">
          <button onClick={() => navigate('/recommendation')}>Recommendation AI</button>
        </div>
        <div className="action-card">
          <button onClick={() => { localStorage.removeItem('token'); navigate('/login'); }}>Logout</button>
        </div>
      </div>
    </div>
  );
}
