import React from 'react';
import { NavLink } from 'react-router-dom';
import '../styles/sidebar.css';

export default function Sidebar({ isOpen, unreadChat, pendingConnections }) {
  // Check if current user is admin
  const isAdmin = localStorage.getItem('isAdmin') === 'true';

  const navItems = [
    { path: '/home', icon: 'üè†', label: 'Home' },
   // { path: '/browse', icon: 'üîç', label: 'Premium Experiences' },
    { path: '/all-places', icon: 'üìç', label: 'All Places' },
    { path: '/suggest-places', icon: 'üí°', label: 'Suggest Places' },
    { path: '/trending', icon: 'üî•', label: 'Trending' },
    { path: '/recommendation', icon: '‚≠ê', label: 'Recommendations' },
    { path: '/dashboard', icon: 'üìä', label: 'Dashboard' },
    { path: '/find-people', icon: 'üîé', label: 'Find People' },
    { path: '/connections', icon: 'üë•', label: 'Connections', badge: pendingConnections },
    { path: '/messages', icon: '‚úâÔ∏è', label: 'Messages' },
    { path: '/reviews', icon: 'üí¨', label: 'Reviews' },
    { path: '/chat', icon: 'üí≠', label: 'Community Chat', badge: unreadChat },
    { path: '/payment', icon: 'üí≥', label: 'Payment' },
  ];

  const adminItems = [
    { path: '/add-experiences', icon: '‚ûï', label: 'Add Experience' },
    { path: '/created-experiences', icon: 'üìù', label: 'My Experiences' },
    { path: '/admin', icon: '‚öôÔ∏è', label: 'Admin Dashboard' },
    { path: '/analysis', icon: 'üìà', label: 'Analytics' },
  ];

  return (
    <>
      <aside className={`sidebar ${isOpen ? 'open' : ''}`}>
        <div className="sidebar-content">
          <div className="sidebar-section">
            <div className="sidebar-section-title">Main Menu</div>
            <nav className="sidebar-nav">
              {navItems.map(item => (
                <NavLink
                  key={item.path}
                  to={item.path}
                  className={({ isActive }) => `sidebar-link ${isActive ? 'active' : ''}`}
                >
                  <span className="sidebar-icon">{item.icon}</span>
                  <span className="sidebar-label">{item.label}</span>
                  {item.badge > 0 && (
                    <span className="sidebar-badge">{item.badge}</span>
                  )}
                </NavLink>
              ))}
            </nav>
          </div>

          {isAdmin && (
            <div className="sidebar-section">
              <div className="sidebar-section-title">Management</div>
              <nav className="sidebar-nav">
                {adminItems.map(item => (
                  <NavLink
                    key={item.path}
                    to={item.path}
                    className={({ isActive }) => `sidebar-link ${isActive ? 'active' : ''}`}
                  >
                    <span className="sidebar-icon">{item.icon}</span>
                    <span className="sidebar-label">{item.label}</span>
                  </NavLink>
                ))}
              </nav>
            </div>
          )}
        </div>
      </aside>
      {isOpen && <div className="sidebar-overlay" />}
    </>
  );
}
