import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllReviews, deleteReview } from '../services/api';

export default function ReviewManage() {
  const navigate = useNavigate();
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [deletingId, setDeletingId] = useState(null);

  const fetch = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await getAllReviews();
      setReviews(data.reviews || []);
    } catch (err) {
      setError(err.message || 'Failed to load reviews');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => { fetch(); }, [fetch]);

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this review?')) return;
    try {
      setDeletingId(id);
      await deleteReview(id);
      setReviews(prev => prev.filter(r => r._id !== id));
    } catch (err) {
      alert(err.message || 'Delete failed');
    } finally {
      setDeletingId(null);
    }
  };

  const filtered = reviews.filter(r => {
    if (!search) return true;
    const q = search.toLowerCase();
    return (r.userId?.name || '').toLowerCase().includes(q) || (r.userId?.email || '').toLowerCase().includes(q);
  });

  return (
    <div className="container" style={{ padding: 20, background: '#000', color: '#fff', minHeight: '60vh' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Review Management</h2>
        <div>
          <button onClick={() => navigate('/admin')} style={{ marginRight: 8 }}>← Back</button>
        </div>
      </div>

      <div style={{ marginTop: 12 }}>
        <input
          placeholder="Search by reviewer name or email"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{
            padding: 8,
            width: '100%',
            maxWidth: 420,
            borderRadius: 6,
            background: '#0f0f0f',
            color: '#fff',
            border: '1px solid #333'
          }}
        />
      </div>

      {loading && <p>Loading reviews...</p>}
      {error && <p style={{ color: 'crimson' }}>{error}</p>}

      {!loading && !error && (
        <div style={{ display: 'flex', flexDirection: 'column', gap: 8, marginTop: 12 }}>
            {filtered.map(r => (
            <div key={r._id} style={{ display: 'flex', alignItems: 'center', gap: 12, padding: 12, border: '1px solid #222', borderRadius: 8, background: '#0b0b0b', color: '#eee' }}>
              <div style={{ flex: 1 }}>
                <div style={{ fontWeight: 700, color: '#fff' }}>{r.userId?.name || 'Anonymous'}</div>
                <div style={{ fontSize: 12, color: '#bbb' }}>{r.userId?.email}</div>
                <div style={{ marginTop: 6, color: '#e6e6e6' }}>{r.reviewText ? r.reviewText.slice(0, 240) : `Rating: ${r.rating || 'N/A'}`}</div>
                <div style={{ marginTop: 6, fontSize: 12, color: '#999' }}>Location: {r.location || '—'} • {new Date(r.createdAt).toLocaleString()}</div>
              </div>
              <div style={{ display: 'flex', gap: 8 }}>
                <button onClick={() => handleDelete(r._id)} disabled={deletingId === r._id} style={{ background: '#c0392b', color: 'white', border: 'none', padding: '6px 10px', borderRadius: 6 }}>
                  {deletingId === r._id ? 'Deleting...' : 'Delete'}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
