import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function CreatedExperiences() {
  const navigate = useNavigate();
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [deletingId, setDeletingId] = useState(null);

  useEffect(() => {
    try {
      setLoading(true);
      const raw = localStorage.getItem('all_places') || '[]';
      const arr = JSON.parse(raw);
      setPlaces(Array.isArray(arr) ? arr : []);
    } catch (err) {
      setError(err.message || 'Failed to load places');
    } finally {
      setLoading(false);
    }
  }, []);

  const handleDelete = async (id, title) => {
    if (!window.confirm(`Delete experience ${title}?`)) return;
    try {
      setDeletingId(id);
      const raw = localStorage.getItem('all_places') || '[]';
      const arr = JSON.parse(raw).filter(p => p.id !== id && p._id !== id);
      localStorage.setItem('all_places', JSON.stringify(arr));
      setPlaces(arr);
    } catch (err) {
      alert(err.message || 'Delete failed');
    } finally {
      setDeletingId(null);
    }
  };

  const filtered = places.filter(p => {
    if (!search) return true;
    const q = search.toLowerCase();
    return (p.title || '').toLowerCase().includes(q) || (p.duration || '').toLowerCase().includes(q) || (p.price || '').toLowerCase().includes(q);
  });

  return (
    <div className="container admin-container" style={{ background: 'black', color: 'white', minHeight: '60vh', padding: 20 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div className="brand"><h2 className="brand-heading">Created Experiences</h2></div>
        <div>
          <button className="back-btn" onClick={() => navigate('/admin')}>Back</button>
        </div>
      </div>

      <p>Only experiences created via the admin form (client-side).</p>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'crimson' }}>{error}</p>}

      {!loading && (
        <div>
          <div style={{ marginTop: 12, display: 'flex', gap: 8, alignItems: 'center' }}>
            <input
              placeholder="Search by title, price or duration"
              value={search}
              onChange={e => setSearch(e.target.value)}
              style={{ padding: 8, width: '100%', maxWidth: 400, borderRadius: 8 }}
            />
            <button onClick={() => setSearch('')} style={{ padding: 8, borderRadius: 8 }}>Clear</button>
          </div>

          <div style={{ overflowX: 'auto', marginTop: 12 }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', color: 'white' }}>
              <thead>
                <tr>
                  <th style={{ textAlign: 'left', borderBottom: '1px solid #444', padding: 8 }}>Title</th>
                  <th style={{ textAlign: 'left', borderBottom: '1px solid #444', padding: 8 }}>Price</th>
                  <th style={{ textAlign: 'left', borderBottom: '1px solid #444', padding: 8 }}>Duration</th>
                  <th style={{ textAlign: 'left', borderBottom: '1px solid #444', padding: 8 }}>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filtered.length === 0 && (
                  <tr><td colSpan={4} style={{ padding: 8 }}>No experiences found.</td></tr>
                )}
                {filtered.map(p => (
                  <tr key={p.id || p._id}>
                    <td style={{ padding: 8, borderBottom: '1px solid #222' }}>{p.title}</td>
                    <td style={{ padding: 8, borderBottom: '1px solid #222' }}>{p.price}</td>
                    <td style={{ padding: 8, borderBottom: '1px solid #222' }}>{p.duration}</td>
                    <td style={{ padding: 8, borderBottom: '1px solid #222' }}>
                      <button
                        onClick={() => handleDelete(p.id || p._id, p.title)}
                        disabled={deletingId === (p.id || p._id)}
                        style={{ background: '#c0392b', color: 'white', border: 'none', padding: '6px 10px', cursor: 'pointer' }}
                      >{deletingId === (p.id || p._id) ? 'Deleting...' : 'Delete'}</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      <div style={{ marginTop: 12 }}>
        <button onClick={() => { localStorage.removeItem('token'); navigate('/login'); }}>Logout</button>
      </div>
    </div>
  );
}
