import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllUsers } from '../services/api';

export default function Analysis() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [search, setSearch] = useState('');
  const [searchBy, setSearchBy] = useState('name');

  useEffect(() => {
    let mounted = true;
    async function load() {
      setLoading(true);
      setError(null);
      try {
        const res = await getAllUsers();
        if (!mounted) return;
        setUsers(res.users || []);
      } catch (err) {
        setError(err.message || 'Failed to load users');
      } finally {
        setLoading(false);
      }
    }
    load();
    return () => { mounted = false; };
  }, []);

  return (
    <div className="container admin-container" style={{ background: 'black', color: 'white', minHeight: '60vh', padding: 20 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div className="brand"><h2 className="brand-heading">Analytics</h2></div>
        <div>
          <button className="back-btn" onClick={() => navigate('/admin')}>Back</button>
        </div>
      </div>
      <h1 style={{ color: 'crimson' }}>Users</h1>
      <div style={{ display: 'flex', gap: 8, marginBottom: 12, alignItems: 'center' }}>
        <select value={searchBy} onChange={e => setSearchBy(e.target.value)} style={{ padding: '8px 10px', borderRadius: 8 }}>
          <option value="name">Name</option>
          <option value="email">Email</option>
        </select>
        <input
          placeholder={`Search by ${searchBy}`}
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{ padding: '8px 10px', borderRadius: 8, flex: 1 }}
        />
        <button onClick={() => setSearch('')} style={{ padding: '8px 10px', borderRadius: 8 }}>Clear</button>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'crimson' }}>{error}</p>}
      {!loading && !error && (
        <div style={{ display: 'grid', gap: 8 }}>
          {users
            .filter(u => {
              if (!search) return true;
              const q = search.toLowerCase();
              if (searchBy === 'email') return (u.email || '').toLowerCase().includes(q);
              return (u.name || '').toLowerCase().includes(q);
            })
            .map(u => (
              <button key={u._id} className="admin-btn" onClick={() => navigate(`/analysis/${u._id}`)} style={{ textAlign: 'left', padding: 10 }}>
                {u.name} : {u.email}
              </button>
            ))}
        </div>
      )}
      <div style={{ marginTop: 16 }}>
        <button className="btn-logout" onClick={() => { localStorage.removeItem('token'); navigate('/login'); }}>Logout</button>
      </div>
    </div>
  );
}
